templates.template:
	/**
	 * The ${field} (lazy initialized)
	 */
	private volatile ${type} ${field};

	/**
	 * Gets the ${field}
	 *
	 * @return the ${field}
	 */
	${visibility:link('public','protected','','private')} ${type} ${get}() {
		${type} result = this.${field};
		if (result == null) {
			synchronized (this) {
				result = this.${field};
				if (result == null) {
					result = new ${type}(${cursor});
					this.${field} = result;
				}
			}
		}
		return result;
	}
